				$('#img_icono_error').show();
				$("#barcodeboleto").prop('disabled', false);
				$.gritter.add({ title : "Atenci&oacute;n", text : 'El boleto <b style="color:#DED9BF;">'+ultimoFolio+'</b> no est&aacute; vendido' });
