var handlecontrollersave = function(){
	
	uploadFile(processFile);
}

function uploadFile(runOnSuccess){
	
	var file_data = $('#inputfileconciliacion').prop('files')[0]; 
	var form_data = new FormData();
	form_data.append('file', file_data);

	$.ajax({
		url : 'ImportNichos',
		dataType : 'text', // what to expect back from the PHP script, if anything
		cache : false,
		contentType : false,
		processData : false,
		data : form_data,
		type : 'post',
		success : function(msg) {
			var data = jQuery.parseJSON(msg);
			if (runOnSuccess!=null && typeof(runOnSuccess)=='function')
				runOnSuccess(data.filename);
		},
		error : function(msg) {
			alert(msg);
		}
	});
	
}



function consultaSectores(){
	var usuario = $("#usuario").val();
	var idsectorU = $("#idsectorU").val();
	$('#div_sec_id').hide();
	$('#img_loading_sec_id').show();
	
	$.ajax({
		type: 'GET',
		url: 'ImportNichos',
		data: 'view=BuscarSector&usuario='+usuario+'&idsectorU='+idsectorU,
		success: function(msg){
			if (msg.indexOf('<option') >= 0)
				$('#select_sector_id').html(msg);
			else
				$('#select_sector_id').html('');
			
			$('#div_sec_id').show();
			$('#img_loading_sec_id').hide();
			$('#div_nic_id').show();
		}
	});
}
