var handleBootstrapWizards=function(){"use strict";$("#wizard").bwizard()};


var FormWizard=function(){"use strict";return{init:function(){handleBootstrapWizards()}}}()




$("#wizard").bwizard({validating:function(e,t){
	
	
	
	if(t.index==0){
		
		if(false===$('form[name="form-wizard"]').parsley().validate("wizard-step-1"))
		    {
			
			return false
			
			}else{
				
				guardar.init();
		 		
		    }
		
		}
	
	}
});


var guardar=function(){"use strict";return{init:function(){ 
	
	handlecontrollersave();
	
}}}()


   var handlecontrollersave = function(){
    	
	var role = $('#role').val();    	
	var descripcion = $('#descripcion').val();
    
	
    	$.ajax({ 
    	    type: "POST", 
    	    cache: false, 
    	    url: "Roles.do",  
    	    data: { role: role, descripcion: descripcion}, 
    	    success: function(msg){  
    	    	
    	    	
    	    	  var idrole = msg;    	    	  
    	    	  $("#rolpermiso").attr("href", "Permisos?idrole="+idrole+"");
    	    	
    	    	
    	    	
    	            } 
    	           });
	
     }










